mapper.select:

	select * from modelinfo  where modelstatus=1 and examine=1
		
			
				and modelname like  CONCAT('%',#{modelBean.modelname},'%')
			
			
				and buildtype=#{modelBean.buildtype}
			
			 
				and resource_one=#{modelBean.resource_one}			
			
			
				and resource_two=#{modelBean.resource_two}
			
			
				and modelprice <= #{modelBean.modelprice}
			
			
				and modelprice > 200
			
			
				and modelprice >=#{startPrice}
			
			
				and modelprice <= #{endPrice}
							
				
			
				order by download desc
						
			
				order by download desc
				
			
				order by starclass desc
						
			
				order by creatTime desc

select a.*,b.username from modelinfo a LEFT JOIN userinfo  b on  a.userid=b.userid

	
		and a.modelname like  CONCAT('%',#{modelBean.modelname},'%')
		
	
		and a.userid=#{modelBean.userid}

SELECT a.*, b.username,c.username examinepeoplename, case when a.examine=0 then '审核中' when a.examine=1 then '审核通过' else '不通过' end examinename FROM modelinfo a LEFT JOIN userinfo b ON a.userid = b.userid LEFT JOIN userinfo c on a.examinepeople=c.userid WHERE a.modelstatus = 1
and a.modelname like CONCAT('%',#{modelBean.modelname},'%')
and a.userid=#{modelBean.userid}
and a.examine=#{modelBean.examine}
and a.mid =#{modelBean.mid}
order by a.creatTime desc

mapper.update:
update modelinfo 

	
		download=#{download}
	
	
		starclass=#{starclass}
	

where mid=#{mid}
