mapper.select:
select a.*,b.username,b.nickname  from excelmanage  a LEFT JOIN userinfo b on a.uid=b.userid 

	
		a.eid=#{eid}
	
	
		a.excelname like CONCAT('%',#{excelname},'%')
	
	
		a.uid=#{uid}
	

order by a.creattime desc

